INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description`, `area`, `zhiwei` ) VALUES  ('61', '23', '大数据时\ 灯具企业如何借势发展', '<p style=\"text-align: center;\"><img src=\"/images/upload/Image/y(2).jpg\" width=\"878\" height=\"393\" alt=\"\" /></p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p style=\"text-align: left;\"><span style=\"color: rgb(102, 102, 102); font-family: ����; line-height: 35px; text-align: start;\">&nbsp; &nbsp; </span><span style=\"font-size: larger;\"><span style=\"color: rgb(102, 102, 102); font-family: ����; line-height: 35px; text-align: start;\">在大数据时代，灯具企业从生产到销售都需要数据支撑才能制定详细的方案，可以说这是一个信息为王的时代，谁拥有庞大的数据库，谁就将推出更能符合市场需求的产品\</span><br style=\"margin: 0px; padding: 0px; list-style: none; color: rgb(102, 102, 102); font-family: ����; line-height: 35px; text-align: start;\" />\r\n<span style=\"color: rgb(102, 102, 102); font-family: ����; line-height: 35px; text-align: start;\">首先，什么是大数据。据专业人士解释\&ldquo;大数\&rdquo;指的是大小超出常规的数据库工具获取、存储、管理和分析能力的数据集或信息资产，它需要新处理模式才能具有更强的决策力、洞察发现力。然而，大数据技术的战略意义不在于掌握庞大的数据信息，而在于对这些含有意义的数据进行专业化处理。换言之，如果把大数据比作一种产业，那么这种产业实现盈利的关键，在于提高对数据的&ldquo;加工能力&rdquo;，通过&ldquo;加工&rdquo;实现数据\&ldquo;增\&rdquo;\</span><br style=\"margin: 0px; padding: 0px; list-style: none; color: rgb(102, 102, 102); font-family: ����; line-height: 35px; text-align: start;\" />\r\n<span style=\"color: rgb(102, 102, 102); font-family: ����; line-height: 35px; text-align: start;\">随着&ldquo;大数\&rdquo;热潮的兴起，灯具企业也看到了大数据的重要性，开始转变策略研\&ldquo;大数\&rdquo;，以求跟上时代步伐。但是有的企业收效甚微，有的企业则凭\&ldquo;大数\&rdquo;的运用而风生水起。其中，作为高端铝合金灯具的领导品牌之一的致尚灯具则是发展较成功的一家企业，这引起了行业的关注。为了解详情，记者采访了致尚灯具相关负责人\</span><br style=\"margin: 0px; padding: 0px; list-style: none; color: rgb(102, 102, 102); font-family: ����; line-height: 35px; text-align: start;\" />\r\n<span style=\"color: rgb(102, 102, 102); font-family: ����; line-height: 35px; text-align: start;\">大数据时代灯具企业如何借势发展</span><br style=\"margin: 0px; padding: 0px; list-style: none; color: rgb(102, 102, 102); font-family: ����; line-height: 35px; text-align: start;\" />\r\n<span style=\"color: rgb(102, 102, 102); font-family: ����; line-height: 35px; text-align: start;\">采访中，致尚灯具负责人告诉记者，要做\&ldquo;大数\&rdquo;的增值加工，企业要有专业的团队，随时关注数据变化，并对数据进行实时存储、管理和分析，让营销人员更好地了解客户，对客户个体进行全面的分析。然后，根据大数据分析的结果，对消费者的喜好进行判定，制定和调整相关策略\</span><br style=\"margin: 0px; padding: 0px; list-style: none; color: rgb(102, 102, 102); font-family: ����; line-height: 35px; text-align: start;\" />\r\n<span style=\"color: rgb(102, 102, 102); font-family: ����; line-height: 35px; text-align: start;\">&ldquo;在信息时代，人们在上网的时候，他的浏览历史、行为习惯以及兴趣爱好都被记录下来，成为互联网的数据。而企业利用大数据来更有效地了解客户的需求，这比以前都更具有针对性和相关性。通过对这些数据的分析，我们不难发现，产品质量和服务永远是消费者关注的重点\&rdquo;致尚负责人如此说道，为客户提供更加优质的产品和个性化的服务能够带来更多的销量，获得客户更多的忠诚度以及拥有优良的口碑。因此，企业根据大数据分析结果，制定更有效的营销策略，准确地对潜在用户需求进行深入挖掘和针对性投入，将更多的重心放在产品和服务上\</span><br style=\"margin: 0px; padding: 0px; list-style: none; color: rgb(102, 102, 102); font-family: ����; line-height: 35px; text-align: start;\" />\r\n<span style=\"color: rgb(102, 102, 102); font-family: ����; line-height: 35px; text-align: start;\">一方面，致尚灯具为打造优质产品，其采用进口硅镁钛铝合金材料，在很大程度上增加铝材的硬度、韧度、强度及光泽度。同时，致尚灯具采用德国进口的五金配件，使产品的使用寿命及推拉手感都达到极致。在产品设计上，致尚灯具斥巨资组建专业的设计团队，坚持以消费者需求为基础设计产品，力求设计新颖，为创造创造高端时尚灯具不断努力。现致尚灯具款式众多，产品种类丰富、可选颜色、材质款式均是同行的三倍以上，可满足不同消费群体的要求\</span><br style=\"margin: 0px; padding: 0px; list-style: none; color: rgb(102, 102, 102); font-family: ����; line-height: 35px; text-align: start;\" />\r\n<span style=\"color: rgb(102, 102, 102); font-family: ����; line-height: 35px; text-align: start;\">另一方面，为给消费者提供用心服务，致尚灯具秉承着&ldquo;周到及时、顾客至\&rdquo;的服务理念，推出了一套完善的服务体系。在售前，企业提供免费测量实地考察之后，为消费者提供合理的家装建议;售中，企业会派人送货上门并安\;售后，致尚灯具承诺五金配\1年免费维护以及终身维修的服务，以求达到消费者最大的满意度，这也为致尚灯具赢得了强大的口碑支持\</span><br style=\"margin: 0px; padding: 0px; list-style: none; color: rgb(102, 102, 102); font-family: ����; line-height: 35px; text-align: start;\" />\r\n<span style=\"color: rgb(102, 102, 102); font-family: ����; line-height: 35px; text-align: start;\">最后，致尚灯具负责人表示，&ldquo;大数\&rdquo;的运用的确给市场带来了很多发展空间，为许多企业了解客户需求并制定策略提供了依据。但是大数据的运用还有待完善，灯具企业要用好大数据，除了要辨清其特性，还需结合自身的实际情况，才能紧跟市场步伐发展\</span></span></p>', '吉采科技', '', '', '0', '1', '1444859107', '', '0', 'http://', '', '', '');
INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description`, `area`, `zhiwei` ) VALUES  ('48', '8', '商品保修', '<p><span style=\"font-size: larger;\"><span style=\"color: rgb(102, 102, 102); font-family: ����; line-height: 35px;\">1、商品保修时间是以什么时间为起点的？</span><br style=\"margin: 0px; padding: 0px; list-style: none; color: rgb(102, 102, 102); font-family: ����; line-height: 35px;\" />\r\n<span style=\"color: rgb(102, 102, 102); font-family: ����; line-height: 35px;\">您好，商品保修时间以您收货时间开始计算\</span></span></p>\r\n<p><span style=\"font-size: larger;\"><span style=\"color: rgb(102, 102, 102); font-family: ����; line-height: 35px;\">2、办理货品维修需要多长时间？</span><br style=\"margin: 0px; padding: 0px; list-style: none; color: rgb(102, 102, 102); font-family: ����; line-height: 35px;\" />\r\n<span style=\"color: rgb(102, 102, 102); font-family: ����; line-height: 35px;\">维修：自接收到问题商品之日起30日内处理完成</span><br style=\"margin: 0px; padding: 0px; list-style: none; color: rgb(102, 102, 102); font-family: ����; line-height: 35px;\" />\r\n<span style=\"color: rgb(102, 102, 102); font-family: ����; line-height: 35px;\">该周期的起始时间以售后登记到货品之日起算的，需要检测的商品时间会相应延\7-15天。国家法定节假日，因部分品牌生产厂家的售后服务中心推迟提供服务，返修时间也相应延长\&nbsp;</span></span></p>', '吉采科技', '', '商品保修', '0', '1', '1444264278', '', '0', 'http://', '商品保修', '', '');
