INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description`, `area`, `zhiwei` ) VALUES  ('40', '13', '公司信息', '<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span style=\"color: rgb(102, 102, 102); font-family: ����; line-height: 35px;\">哈尔滨吉采电子商务有限公司是黑龙江省政府、哈尔滨市政府支持设立的覆盖黑龙江省全境的大型专业采购平台，大力扶持的建材、家具、家居实体与互联网结合的公司，作\&ldquo;现代网络销售与服务平台&rdquo;，致力于开发并提供企业对企业、企业对客户的网络平台，为广大客户提供线上服务，线下服务\ 吉采平台在全省范围内拥有700加盟商（含地级市、县级市乡镇、农场、林区）覆盖全省吉采电子商务联盟，现面向全国招商：建筑材料、装饰材料、灯具、家具、家居等商品，打造全新未来装修、装饰一条龙新模式。我们秉承公开、公平、民主的合作理念与各商家一道，推广宣传您的商品，让消费者离我们更近，让商业更简单。在这个国家大力支持互联网企业的时代，祝愿您的企业或产品通过我们吉采电子商务平台，快速占领黑龙江市场并长期保有一席之地。我们拥有优秀技术人员，客服、渠道人员。做好宣传本地企业，为您的产品的市场占有率做最坚强的后\.</span></p>', '吉采科技', '', '公司信息', '0', '1', '1444228053', '', '0', 'http://', '公司信息', '', '');
